package publics.service;

import lombok.AllArgsConstructor;
import org.springframework.stereotype.Service;
import publics.model.Image;
import publics.repository.ImageRepo;

import java.util.ArrayList;
import java.util.List;

@AllArgsConstructor
@Service
public class ImageService {
    private final ImageRepo repo;

    public List<Image> getAllClients(){
        return repo.findAll();
    }
    public Image postimage(Image image){
        return repo.save(image);
    }
    public List<Image> getByIdEnchere(int idEnchere){
        ArrayList<Image> img=new ArrayList<>();
        ArrayList<Image> ls=new ArrayList<>(repo.findAll());
        for(int r=0;r<ls.size();r++){
            if(ls.get(r).getIdenchere()==idEnchere)
                img.add(ls.get(r));
        }
        return img;
    }
    public Image findByIdenchere(int idenchere){
        Image a=repo.findByIdenchere(idenchere);
        String [] str=new String[1];
        str[0]="data:image/png;base64,";
        if(a==null||a.getPhoto().length==0){
            Image im=new Image(idenchere,str);
            return im;
        }
        return a;
    }
}
